const dataCenter = []
const dataOuter = []

const fieldsCenter = ["HC", "HP"]
const fieldsOuter = ["Car", "Water", "Heaters", "Plugs", "Others"]

data.series.map((s) => {
  if (fieldsCenter.includes(s.refId)) {
    value = s.fields.find((f) => f.type === 'number').values.buffer;
    value = Number(value).toFixed(2)
    if (s.refId.includes("HC")) {
      cost = 0.1615
    } else {
      cost = 0.2228
    }
    valueCost = Number(value * cost).toFixed(2)
    dataCenter.push({ value: value, name: s.refId, cost: valueCost });
  }
  if (fieldsOuter.includes(s.refId)) {
    value = s.fields.find((f) => f.type === 'number').values.buffer;
    value = Number(value).toFixed(2)
    if (s.fields[0].name.includes('OnOff')) {
      device = s.fields[0].name.replace(/P(.*)~(.*)~OnOff/, '$2')
      cost = 0.2062
    } else if (s.refId.includes("Others")) {
      device = s.refId
      cost = 0.2062
    } else {
      device = s.refId
      cost = 0.1615
    }
    valueCost = Number(value * cost).toFixed(2)
    dataOuter.push({ value: value, name: device, cost: valueCost });
  }
});

