const barData = {
  HPJB: { color: '#6495ED', cost: 0.1249 },
  HCJB: { color: '#87CEFA', cost: 0.0970 },
  HPJW: { color: '#B0C4DE', cost: 0.1508 },
  HCJW: { color: '#F8F8FF', cost: 0.1140 },
  HPJR: { color: '#B22222', cost: 0.6712 },
  HCJR: { color: '#F08080', cost: 0.1216 }
}

var tooltipCallback = (args) => {
  // console.log(args)
  let date = new Date(parseInt(args.data[0]))
  return date.toDateString().slice(0, 10) + '<br />' + args.seriesName + ' ' + Number(args.data[1] / 1000).toFixed(2) + ' kWh';
}

const series = data.series.map((s) => {
  const name = s.refId.replace("Teleinfo-BBR", "")

  const sData = s.fields.find((f) => f.type === 'number').values.buffer;
  const sTime = s.fields.find((f) => f.type === 'time').values.buffer;
  const color = barData[name].color;

  return {
    name: name,
    type: 'bar',
    stack: true,
    showSymbol: false,
    areaStyle: {
      opacity: 0.1,
    },
    lineStyle: {
      width: 1,
    },
    data: sData.map((d, i) => [sTime[i], d.toFixed(2)]),
    color: color,
    tooltip: {
      trigger: 'item',
      axisPointer: {
        type: 'shadow',
      },
      formatter: tooltipCallback
    }
  };
});

// top stack bar label
series[series.length - 1].label = {
  normal: {
    show: true,
    position: 'top',
    color: "white",
    fontSize: 12,
    rotate: 50,
    formatter: (params) => {
      let timelineTotal = replaceVariables("${timelineTotal}");
      let display = ""
      switch (timelineTotal) {
        case 'kWh':
          let total = 0;
          series.forEach(serie => {
            // console.log(serie.data[params.dataIndex])
            total += parseInt(serie.data[params.dataIndex][1]);
          })
          display = "                 " + (total / 1000).toFixed(1) + " kWh"
          break;
        case 'base':
          let totalBase = 0;
          series.forEach(serie => {
            // console.log(serie.data[params.dataIndex])
            totalBase += parseInt(serie.data[params.dataIndex][1]);
          })
          let costTotal = (totalBase * 0.2062 / 1000).toFixed(1)
          display = "                 " + costTotal + " €";
          break
        case 'blue':
          let totalHC = 0;
          let totalHP = 0;
          series.forEach(serie => {
            // console.log(serie.data[params.dataIndex])
            if (serie.name.indexOf('HC') >= 0)
              totalHC += parseInt(serie.data[params.dataIndex][1]);
            else
              totalHP += parseInt(serie.data[params.dataIndex][1]);
          })
          let costTotalBlue = ((totalHC * 0.1615 / 1000) + (totalHP * 0.2228 / 1000)).toFixed(1)
          display = "                 " + costTotalBlue + " €";
          break
        case 'tempo':
          let totalBBR = {};
          series.forEach(serie => {
            // console.log(serie.data[params.dataIndex])
            totalBBR[serie.name] = parseInt(serie.data[params.dataIndex][1]);
          })
          let costTotalTempo = (
            (totalBBR['HPJB'] * barData['HPJB'].cost / 1000)
            + (totalBBR['HCJB'] * barData['HCJB'].cost / 1000)
            + (totalBBR['HPJW'] * barData['HPJW'].cost / 1000)
            + (totalBBR['HCJW'] * barData['HCJW'].cost / 1000)
            + (totalBBR['HPJR'] * barData['HPJR'].cost / 1000)
            + (totalBBR['HCJR'] * barData['HCJR'].cost / 1000)
          ).toFixed(1)
          display = "                 " + costTotalTempo + " €";
          break
      }
      return display
    }
  }
}

