genStack = function (stackName) {
  if (stackName == 'main') return '45%'
  else return '20%'
}

const series = context.panel.data.series.map((s) => {
  // console.log(s)

  const name = s.fields.find((f) => f.type === 'number').config.displayNameFromDS
    .replace(/P(.*)~(.*)~OnOff/, '$2')
    .replace(/Teleinfo-BBR(.*)/, '$1')

  let sData = s.fields.find((f) => f.type === 'number').values.buffer || s.fields.find((f) => f.type === 'number').values;

  // Move 'Grid Feed' to the negative quadrant
  if (s.refId === "MainToGrid") {
    sData = sData.map(function (x) {
      return x * -1.0;
    });
  }

  let sTime = s.fields.find((f) => f.type === 'time').values.buffer || s.fields.find((f) => f.type === 'time').values;

  return {
    name: name,
    type: 'bar',
    stack: barData[name].stack,
    barWidth: genStack(barData[name].stack),
    color: barData[name].color,
    showSymbol: false,
    areaStyle: {
      opacity: 0.1,
    },
    lineStyle: {
      width: 1,
    },
